import React from "react";
import Slide from "./menu";
import THREEBOX from "./three.js";

const Control = () => {
  const closeBox = (clb) => {
    console.log(Close);
    return THREEBOX.rotations1();
  };

  const openBox = (opb) => {
    console.log(Open);
    return THREEBOX.rotations2();
  };

  const showShape = () => {
    return THREEBOX.main();
  };

  return (
    <fragment>
      {showShape()}
      <Slide clb={closeBox} opb={openBox} />
    </fragment>
  );
};

export default Control;
