import React, { useState } from "react";
import { Slider, InputNumber, Row, Col, Button } from "antd";
import "antd/dist/antd.css";

const Menu = (props) => {
  const { clb, opb, size } = props;

  const [inputAvalue, setinputAvalue] = useState(0);
  const [inputBvalue, setinputBvalue] = useState(0);
  const [inputCvalue, setinputCvalue] = useState(0);

  const onChangeA = (value) => {
    console.log(value);
    setinputAvalue(value);
    return size(value, inputBvalue, inputCvalue);
  };
  const onChangeB = (value) => {
    console.log(value);
    setinputBvalue(value);
    return size(inputAvalue, value, inputCvalue);
  };
  const onChangeC = (value) => {
    console.log(value);
    setinputCvalue(value);
    return size(inputAvalue, inputAvalue, value);
  };

  const onClick1 = () => {
    return clb("พับกล่อง");
  };

  const onClick2 = () => {
    return opb("แกะกล่อง");
  };

  return (
    <div>
      <Button type="primary" onClick={onClick1}>
        พับกล่อง
      </Button>
      <Button onClick={onClick2}>แกะกล่อง</Button>
      <br />
      {/* A */}
      <Row>
        <Col span={12}>
          <Slider
            min={1}
            max={200}
            onChange={onChangeA}
            value={typeof inputValue === "number" ? inputAvalue : 0}
          />
        </Col>
        <Col span={4}>
          <InputNumber
            min={1}
            max={200}
            style={{ margin: "0 16px" }}
            value={inputAvalue}
            onChange={onChangeA}
          />
        </Col>
      </Row>
      {/* B */}
      <Row>
        <Col span={12}>
          <Slider
            min={1}
            max={200}
            onChange={onChangeB}
            value={typeof inputBValue === "number" ? inputBvalue : 0}
          />
        </Col>
        <Col span={4}>
          <InputNumber
            min={1}
            max={200}
            style={{ margin: "0 16px" }}
            value={inputBvalue}
            onChange={onChangeB}
          />
        </Col>
      </Row>
      {/* C */}
      <Row>
        <Col span={12}>
          <Slider
            min={1}
            max={200}
            onChange={onChangeC}
            value={typeof inputCValue === "number" ? inputCvalue : 0}
          />
        </Col>
        <Col span={4}>
          <InputNumber
            min={1}
            max={200}
            style={{ margin: "0 16px" }}
            value={inputCvalue}
            onChange={onChangeC}
          />
        </Col>
      </Row>
      <hr />
    </div>
  );
};

export default Menu;
