import React, { Fragment } from "react";
import * as THREE from "three";
import OrbitControls from "three-orbitcontrols";

var controls, renderer, scene, camera;
var A = 100;
var B = 200;
var C = 52;
var D = 0.5;
var w = window.innerWidth;
var h = window.innerHeight;
var L = 0.3;
var leng_lr_lib = A * L; // ลิ้นกันฝุ่น

const Three = () => {
  const init = () => {
    scene = new THREE.Scene();
    // scene.background = new THREE.Color(0xffffff);
    scene.background = new THREE.Color(0x000000);

    //The smaller the first number is, the closer the cube appears
    camera = new THREE.PerspectiveCamera(50, w / h, 1, 5000);
    camera.position.x = 0;
    camera.position.y = 0;
    camera.position.z = 800;

    const axesHelper = new THREE.AxesHelper(100);
    scene.add(axesHelper);

    const material = new THREE.MeshBasicMaterial({
      color: "#FFFFFF",
      side: THREE.DoubleSide,
      wireframe: true,
    });

    var plane_side_A = new THREE.BoxGeometry(A, C, D);
    var plane_side_B = new THREE.BoxGeometry(B, C, D);
    var plane_top_bottom = new THREE.BoxGeometry(A, B, D);

    // Mark 1
    var lr_lid_shape = new THREE.Shape();
    lr_lid_shape.moveTo(0, 0);
    // Front ....................................................
    lr_lid_shape.lineTo(0, (leng_lr_lib * 0.1) | 0);
    lr_lid_shape.lineTo(A * 0.05, (leng_lr_lib * 0.15) | 0);
    lr_lid_shape.lineTo(A * 0.05, (leng_lr_lib * 0.7) | 0);
    lr_lid_shape.bezierCurveTo(
      A * 0.05,
      leng_lr_lib,
      A * 0.1,
      leng_lr_lib,
      A * 0.15,
      leng_lr_lib
    );
    // Center ...................................................
    lr_lid_shape.lineTo(A * 0.95, leng_lr_lib);
    // Back .....................................................
    lr_lid_shape.lineTo(A * 0.95, (leng_lr_lib * 0.1) | 0);
    lr_lid_shape.lineTo(A, 0);

    //Mark 2
    var lr_lid_shape_d = new THREE.Shape();
    lr_lid_shape_d.moveTo(0, 0); //เริ่มต้นที่จุด x = 0, y=0
    lr_lid_shape_d.lineTo(A | 0, 0); //ด้านตรงข้ามมุมฉาก y // 0, 24
    lr_lid_shape_d.lineTo(A, A);
    lr_lid_shape_d.lineTo((A * -0) | 0, A);

    //Mark 3
    var lr_lid_shape_d_s = new THREE.Shape();
    lr_lid_shape_d_s.moveTo(0, 0);
    // Front ....................................................
    lr_lid_shape_d_s.lineTo(A * 0.15, (leng_lr_lib * 0.9) | 0);
    lr_lid_shape_d_s.lineTo(A * 0.2, leng_lr_lib);
    // Center ...................................................
    lr_lid_shape_d_s.lineTo(A * 0.8, leng_lr_lib);
    // Back .....................................................
    lr_lid_shape_d_s.lineTo(A * 0.85, (leng_lr_lib * 0.9) | 0);
    // Rear......................................................
    lr_lid_shape_d_s.lineTo(A, 0);

    var lr_lid_d = new THREE.ShapeGeometry(lr_lid_shape); //Mark1
    var lr_lid_ds = new THREE.ShapeGeometry(lr_lid_shape_d); //Mark2
    var lr_lid_dss = new THREE.ShapeGeometry(lr_lid_shape_d_s); //Mark3

    //* ลิ้นล่างซ้าย
    // var side_pp_left_d = new THREE.Mesh(lr_lid_d, material);
    //side_pp_left_d.rotation.x = Math.PI; //หมุนแกน 180 องศา
    // side_pp_left_d.position.x = 100;
    // scene.add(side_pp_left_d);

    var side_A = new THREE.Mesh(plane_side_A, material);
    side_A.position.x = -50;

    var side_A_front = new THREE.Mesh(plane_side_A, material);
    side_A_front.position.x = -250;

    var side_B_right = new THREE.Mesh(plane_side_A, material);
    side_B_right.position.x = 50;

    var side_B_left = new THREE.Mesh(plane_side_A, material);
    side_B_left.position.x = -150;

    //ลิ้นบน 1
    var side_pp_right_d = new THREE.Mesh(lr_lid_d, material);
    var pivot_pp_lid_right = new THREE.Object3D();
    pivot_pp_lid_right.position.set(0, 25, 0);
    pivot_pp_lid_right.add(side_pp_right_d);

    //ลิ้นล่าง 1 *
    var side_pp_left_d = new THREE.Mesh(lr_lid_d, material);
    var pivot_pp_lid_left = new THREE.Object3D();
    pivot_pp_lid_left.rotation.x = Math.PI; //หมุนแกน 180 องศา
    pivot_pp_lid_left.position.set(0, -25, 0);
    pivot_pp_lid_left.add(side_pp_left_d);

    //แกนกลาง 1
    var pivot_B_right = new THREE.Object3D();
    pivot_B_right.position.set(0, 0, 0);
    pivot_B_right.add(
      side_B_right,
      pivot_pp_lid_right
      // pivot_pp_lid_left
    );

    //ลิ้นบนบน 2*
    var side_bb_right_d = new THREE.Mesh(lr_lid_dss, material);
    var pivot_bb_lid_right = new THREE.Object3D();
    pivot_bb_lid_right.position.set(-100, 125, 0);
    pivot_bb_lid_right.add(side_bb_right_d);

    //ลิ้นบน 2*
    var side_b_right_d = new THREE.Mesh(lr_lid_ds, material);
    var pivot_b_lid_right = new THREE.Object3D();
    pivot_b_lid_right.position.set(-100, 25, 0);
    pivot_b_lid_right.add(side_b_right_d);

    //ลิ้นล่าง 2*
    var side_b_left_d = new THREE.Mesh(lr_lid_d, material);
    var pivot_b_lid_left = new THREE.Object3D();
    pivot_b_lid_left.rotation.x = Math.PI; //หมุนแกน 180 องศา
    pivot_b_lid_left.position.set(-100, -25, 0);
    pivot_b_lid_left.add(side_b_left_d);

    //แกนกลาง 2
    var pivot_center = new THREE.Object3D();
    pivot_center.position.set(0, 0, 0);
    pivot_center.add(
      side_A,
      pivot_bb_lid_right,
      pivot_b_lid_right
      // pivot_b_lid_left
    );

    // scene.add(pivot_center, pivot_B_right, pivot_B_left);
    scene.add(pivot_center, pivot_A_front, pivot_B_right);

    //* Webgl Render
    renderer = new THREE.WebGLRenderer();
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(w, h);
    document.getElementById("webgl").append(renderer.domElement);

    //* The mouse controls.
    controls = new OrbitControls(camera, renderer.domElement);
    controls.minZoom = 0.5;
    controls.maxZoom = 10;
  };

  const animate = () => {
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
  };

  return (
    <Fragment>
      {init()}
      {animate()}
    </Fragment>
  );
};

export default Three;
