import React, { Fragment } from "react";
import * as THREE from "three";
import OrbitControls from "three-orbitcontrols";

var controls, renderer, scene, camera;
var A = 200;
var B = 100;
var C = 52;
var D = 0.5;
var w = window.innerWidth;
var h = window.innerHeight;
var L = 0.3;
var P = 5;
var leng_lr_lid = A * L; // ลิ้นกันฝุ่น

const Three = () => {
  const init = () => {
    scene = new THREE.Scene();
    scene.background = new THREE.Color(0x000000);

    //The smaller the first number is, the closer the cube appears.
    camera = new THREE.PerspectiveCamera(50, w / h, 1, 5000);
    camera.position.x = 0;
    camera.position.y = 0;
    camera.position.z = 800;

    const axesHelper = new THREE.AxesHelper(100);
    scene.add(axesHelper);

    const material = new THREE.MeshBasicMaterial({
      color: "#FFFFFF",
      side: THREE.DoubleSide,
      wireframe: true,
    });

    var plane_side_A = new THREE.BoxGeometry(A, C, D);
    var plane_side_B = new THREE.BoxGeometry(B, C, D);
    var plane_top_buttom = new THREE.BoxGeometry(A, B, D);

    // ******************* Shape Modify ******************* //

    // ฝาเสียบ
    var lid = new THREE.Shape();
    lid.moveTo(0, 0);
    lid.lineTo(A, 0);
    lid.bezierCurveTo(A, 0, A - A / 35, -(P - P / 35), A - A / 10, -P);
    lid.lineTo(A / 10, -P);
    lid.bezierCurveTo(A / 10, -P, A / 35, -(P - P / 35), 0, 0);

    var lid = new THREE.ShapeGeometry(lid);

    // ฝาเสียบข้าง
    var lid_ground = new THREE.Shape();
    lid_ground.moveTo(0, 0);
    lid_ground.lineTo(C, 0);
    lid_ground.bezierCurveTo(C, 0, C - C / 35, -(P - P / 35), C - C / 10, -P);
    lid_ground.lineTo(C / 10, -P);
    lid_ground.bezierCurveTo(C / 10, -P, C / 35, -(P - P / 35), 0, 0);

    var lid_ground = new THREE.ShapeGeometry(lid_ground);

    // ลิ้นขวาบน
    var lr_lid = new THREE.Shape();
    lr_lid.moveTo(0, 0);
    // Front ....................................................
    lr_lid.lineTo(0, (leng_lr_lid * 0.1) | 0);
    lr_lid.lineTo(A * 0.05, (leng_lr_lid * 0.15) | 0);
    lr_lid.lineTo(A * 0.05, (leng_lr_lid * 0.7) | 0);
    lr_lid.bezierCurveTo(
      A * 0.05,
      leng_lr_lid,
      A * 0.1,
      leng_lr_lid,
      A * 0.15,
      leng_lr_lid
    );
    // Center ...................................................
    lr_lid.lineTo(A * 0.95, leng_lr_lid);
    // Back .....................................................
    lr_lid.lineTo(A * 0.95, (leng_lr_lid * 0.1) | 0);
    lr_lid.lineTo(A, 0);
    lr_lid.lineTo(0, 0);

    var lr_lid_d = new THREE.ShapeGeometry(lr_lid);

    // ลิ้นขวาล่าง
    var lr_lid_buttom = new THREE.Shape();
    lr_lid_buttom.moveTo(0, 0);
    // Front ....................................................
    lr_lid_buttom.lineTo(0, (A * 0.47) | 0);
    lr_lid_buttom.lineTo((A * 0.4) | 0, (A * 0.47) | 0); 
    // Center .....................................................
    lr_lid_buttom.bezierCurveTo(
      (A * 0.4) | 0,
      (A * 0.47) | 0,
      (A * 0.5) | 0,
      (A * 0.38) | 0,
      (A * 0.4) | 0,
      (A * 0.29) | 0
    );
    // Back .....................................................
    lr_lid_buttom.lineTo(A, 0);
    lr_lid_buttom.lineTo(0, 0);

    var lr_lid_buttom_d = new THREE.ShapeGeometry(lr_lid_buttom);

    // ลิ้นล่าง
    var lr_buttom = new THREE.Shape();
    lr_buttom.moveTo(0, 0);
    // Front ....................................................
    lr_buttom.lineTo(0, A | 0);
    lr_buttom.lineTo((A * 0.5) | 0, A);
    lr_buttom.lineTo((A * 0.5) | 0, (A * 0.75) | 0);
    // Center .....................................................
    lr_buttom.lineTo((A * 0.25) | 0, (A * 0.75) | 0);
    lr_buttom.lineTo((A * 0.25) | 0, (A * 0.25) | 0);
    lr_buttom.lineTo((A * 0.5) | 0, (A * 0.25) | 0);
    // Back .....................................................
    lr_buttom.lineTo((A * 0.5) | 0, 0);

    var lr_lid_buttom = new THREE.ShapeGeometry(lr_buttom);

    // ลิ้นซ้ายล่าง
    var lr_buttom_lid = new THREE.Shape();
    lr_buttom_lid.moveTo(0, 0);
    // Front ....................................................
    lr_buttom_lid.lineTo(0, A | 0);
    lr_buttom_lid.lineTo((A * 0.3) | 0, (A * 0.7) | 0);
    // Center .....................................................
    lr_buttom_lid.lineTo((A * 0.47) | 0, (A * 0.7) | 0);
    lr_buttom_lid.lineTo((A * 0.47) | 0, (A * 0.3) | 0);
    // Back .....................................................
    lr_buttom_lid.lineTo((A * 0.3) | 0, (A * 0.3) | 0);

    var lr_buttom_lid = new THREE.ShapeGeometry(lr_buttom_lid);

    // ******************* A-B_Front_Back_Right_Left ******************* //

    // B_RIGHT
    var side_right = new THREE.Mesh(plane_side_A, material);
    side_right.position.x = A | 0;
    // side_right.position.y = (B * 0.25) | 0;

    // A_BACK
    var pivot_front = new THREE.Mesh(plane_side_A, material);
    scene.add(pivot_front);

    // B_LEFT
    var side_B_left = new THREE.Mesh(plane_side_A, material);
    side_B_left.position.x = (A * -1) | 0;

    // A_FRONT
    var side_A_front = new THREE.Mesh(plane_side_A, material);
    side_A_front.position.x = (A * -2) | 0;

    // ******************* Pivot ******************* //

    // ******************* Pivot_Back *******************

    //Pivot_Buttom_Lid
    var pivot_buttom_lid = new THREE.Mesh(lr_buttom_lid, material);
    pivot_buttom_lid.position.set((A * -2) | 0, 0, 0);
    pivot_buttom_lid.rotation.z = Math.PI / 2;
    pivot_buttom_lid.rotation.x = Math.PI;

    // Pivot_Glue_Lid
    var pivot_glue_lid = new THREE.Mesh(lid_ground, material);
    pivot_glue_lid.position.set((A * -3) | 0, 0, 0);
    pivot_glue_lid.rotation.z = Math.PI / 2;
    pivot_glue_lid.rotation.y = Math.PI;

    var pivot_back = new THREE.Object3D();
    pivot_back.add(
      side_A_front,
      pivot_buttom_lid,
      pivot_glue_lid,
      );

    // ******************* Pivot_Group_Right  *******************

    // Pivot_Right_Lid
    var pivot_right_lid = new THREE.Mesh(lr_lid_d, material);
    pivot_right_lid.position.set(0, C, 0);
    pivot_right_lid.rotation.y = Math.PI;

    // Pivot_Right_Lid_D
    var pivot_right_lid_d = new THREE.Mesh(lr_lid_buttom_d, material);
    pivot_right_lid_d.position.set(A, 0, 0);
    pivot_right_lid_d.rotation.x = Math.PI;
    pivot_right_lid_d.rotation.y = Math.PI;

    var pivot_group_right = new THREE.Object3D();
    pivot_group_right.add(
      side_right,
      pivot_right_lid,
      pivot_right_lid_d
    );

    // ******************* Pivot_Group_Left  *******************

    // Pivot_Lid_Left
    var pivot_lid_left = new THREE.Mesh(lr_lid_d, material);
    pivot_lid_left.position.set((A * -2) | 0, C, 0);

    // Pivot_Lid_Left_D
    var pivot_lid_left_d = new THREE.Mesh(lr_lid_buttom_d, material);
    pivot_lid_left_d.position.set((A * -2) | 0, 0, 0);
    pivot_lid_left_d.rotation.x = Math.PI;

    var pivot_group_left = new THREE.Object3D();
    pivot_group_left.add(
      side_B_left,
      pivot_lid_left,
      pivot_lid_left_d
    );

    // ******************* Pivot_Group_Top *******************

    // Pivot_Top
    var pivot_top = new THREE.Mesh(plane_top_buttom, material);
    pivot_top.position.set((A * -0.5) | 0, B, 0);

    // Pivot_Top_Lid
    var pivot_top_lid = new THREE.Mesh(lid, material);
    pivot_top_lid.position.set((A * -1) | 0, (B * 1.5) | 0, 0);
    pivot_top_lid.rotation.x = Math.PI;

    // Pivot_Back_Lid
    // var pivot_back_lid = new THREE.Mesh(lr_lid_buttom, material);
    // pivot_back_lid.position.set((A * -0) | 0, 0, 0);
    // pivot_back_lid.rotation.x = Math.PI;
    // pivot_back_lid.rotation.z = Math.PI/2;

    var pivot_group_top = new THREE.Object3D();
    pivot_group_top.add(
      pivot_top_lid,
      pivot_top, 
      // pivot_back_lid
    );

    scene.add(
      // pivot_back, // side_A_front
      // pivot_group_right, // side_right, pivot_right_lid, pivot_right_lid_d
      // pivot_group_left, // side_B_left, pivot_left_lid, pivot_left_lid_d, pivot_front
         // pivot_front, // pivot_glue_lid
         // pivot_buttom // pivot_buttom, pivot_buttom_lid
      // pivot_group_top, // pivot_top, pivot_top_lid

      pivot_back,
      // - side_A_front
      pivot_group_right,
      /*
         - side_right
         - pivot_right_lid
         - pivot_right_lid_d
      */
      pivot_group_left,
      /*
         - side_B_left
         - pivot_left_lid
         - pivot_left_lid_d
         - pivot_front
         - pivot_buttom_lid
         - pivot_glue_lid */
      pivot_group_top,
      /*
        - pivot_top
        - pivot_top_lid
      */
    );

    // Webgl Render
    renderer = new THREE.WebGLRenderer();
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(w, h);
    document.getElementById("webgl").append(renderer.domElement);

    // The mouse controls.
    controls = new OrbitControls(camera, renderer.domElement);
    controls.minZoom = 0.5;
    controls.maxZoom = 10;
  };

  const animate = () => {
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
  };

  return (
    <Fragment>
      {init()}
      {animate()}
    </Fragment>
  );
};

export default Three;
