import React, { Fragment } from "react";

/* Router */

import { Route, Switch } from "react-router-dom";

/* Import Component and Pages */

import Menu from "./menu";

import THREEBOX from "./three";
import CARRYBOX from "./carrybox/carry";
import FOODBOX from "./foodbox/food";
import TRAYBOX from "./traybox/tray";
import TRAYSBOX from "./traybox/trays";
import SHIRTBOX from "./shirtbox/shirt";
import UNTITLEDBOX from "./untitledbox/untitled";
import UNTITLED2BOX from "./untitled2box/untitled2";
import THREEJSLOCKBOX from "./threeJSlockbox/threeJSlock";
import THREEJS2LOCKBOX from "./threeJSlockbox/threeJS2lock";
import THREEJS3LOCKBOX from "./threeJSlockbox/threeJS3lock";

const App = () => {
  return (
    <Switch>
      <Route exact path="/" component={THREEBOX} />
      <Route exact path="/carry" component={CARRYBOX} />
      <Route exact path="/food" component={FOODBOX} />
      <Route exact path="/tray" component={TRAYBOX} />
      <Route exact path="/trays" component={TRAYSBOX} />
      <Route exact path="/shirt" component={SHIRTBOX} />
      <Route exact path="/untitled" component={UNTITLEDBOX} />
      <Route exact path="/untitled2" component={UNTITLED2BOX} />
      <Route exact path="/threelock" component={THREEJSLOCKBOX} />
      <Route exact path="/three2lock" component={THREEJS2LOCKBOX} />
      <Route exact path="/three3lock" component={THREEJS3LOCKBOX} />
    </Switch>
  );
};

const Control = () => {
  // Button
  const closeBox = (clb) => {
    // console.log("Close");
    return THREEJS3LOCKBOX.rotations1();
  };

  const openBox = (opb) => {
    // console.log("Open");
    return THREEJS3LOCKBOX.rotations2();
  };

  // Slider
  const sizeShape = (a, b, c) => {
    // console.log(`${a} ${b} ${c}`);
    return THREEJS3LOCKBOX.updateSize(a, b, c);
  };

  // showShape
  const showShape = () => {
    return THREEJS3LOCKBOX.main();
  };

  return (
    <Fragment>
      {showShape()}
      <Menu clb={closeBox} opb={openBox} size={sizeShape} />
    </Fragment>
  );
};

export default {
  App,
  Control,
};
