import React, { useState } from "react";
import { Slider, InputNumber, Row, Col, Button } from "antd";
import "antd/dist/antd.css";
import { rotations1, rotations2 } from "./three";

const menu = () => {
  const [inputValue, setinputValue] = useState(0);

  const onChange = (value) => {
    if (isNaN(value)) {
      return;
    }
    setinputValue({
      inputValue: value,
    });
  };

  return (
    <div>
      <Button type="primary" onClick={rotations1}>
        พับกล่อง
      </Button>
      <Button onClick={rotations2}>แกะกล่อง</Button>
      <br />
      <Row>
        <Col span={12}>
          <Slider
            min={0}
            max={1}
            onChange={onChange}
            value={typeof inputValue === "number" ? inputValue : 0}
            step={0.01}
          />
        </Col>
        <Col span={4}>
          <InputNumber
            min={0}
            max={1}
            style={{ margin: "0 16px" }}
            step={0.01}
            value={inputValue}
            onChange={onChange}
          />
        </Col>
      </Row>
      <hr />
    </div>
  );
};

export default ThreeT;
