import React, { Fragment } from "react";
import * as THREE from "three";
import OrbitControls from "three-orbitcontrols";

var controls, renderer, scene, camera;
var A = 100;
var B = 100;
var C = 52;
var D = 0.5;
var w = window.innerWidth;
var h = window.innerHeight;
var L = 0.3;
var P = 5;
var leng_lr_lid = A * L; // ลิ้นกันฝุ่น

const Three = () => {
  const init = () => {
    scene = new THREE.Scene();
    // scene.background = new THREE.Color(0xffffff);
    scene.background = new THREE.Color(0x000000);

    //The smaller the first number is, the closer the cube appears
    camera = new THREE.PerspectiveCamera(50, w / h, 1, 5000);
    camera.position.x = 0;
    camera.position.y = 0;
    camera.position.z = 800;

    const axesHelper = new THREE.AxesHelper(100);
    scene.add(axesHelper);

    const material = new THREE.MeshBasicMaterial({
      color: "#FFFFFF",
      side: THREE.DoubleSide,
      wireframe: false,
    });

    var plane_side_A = new THREE.BoxGeometry(A, C, D);
    var plane_side_B = new THREE.BoxGeometry(B, C, D);
    var plane_top_bottom = new THREE.BoxGeometry(A, B, D);

    //******************* Shape Modify *******************

    // ฝาเสียบ
    var lid = new THREE.Shape();
    lid.moveTo(0, 0);
    lid.lineTo(A, 0);
    lid.bezierCurveTo(A, 0, A - A / 35, -(P - P / 35), A - A / 10, -P);
    lid.lineTo(A / 10, -P);
    lid.bezierCurveTo(A / 10, -P, A / 35, -(P - P / 35), 0, 0);
    var lid = new THREE.ShapeGeometry(lid);

    // ฝาเสียบล่าง
    var lid_ground = new THREE.Shape();
    lid_ground.moveTo(0, 0);
    lid_ground.lineTo(C, 0);
    lid_ground.bezierCurveTo(C, 0, C - C / 35, -(P - P / 35), C - C / 10, -P);
    lid_ground.lineTo(C / 10, -P);
    lid_ground.bezierCurveTo(C / 10, -P, C / 35, -(P - P / 35), 0, 0);
    var lid_ground = new THREE.ShapeGeometry(lid_ground);

    // ลิ้นขวาบน
    var lr_lid = new THREE.Shape();
    lr_lid.moveTo(0, 0);
    // Front ....................................................
    lr_lid.lineTo(0, (leng_lr_lid * 0.1) | 0);
    lr_lid.lineTo(B * 0.05, (leng_lr_lid * 0.15) | 0);
    lr_lid.lineTo(B * 0.05, (leng_lr_lid * 0.7) | 0);
    lr_lid.bezierCurveTo(
      B * 0.05,
      leng_lr_lid,
      B * 0.1,
      leng_lr_lid,
      B * 0.15,
      leng_lr_lid
    );
    // Center ...................................................
    lr_lid.lineTo(B * 0.95, leng_lr_lid);
    // Back .....................................................
    lr_lid.lineTo(B * 0.95, (leng_lr_lid * 0.1) | 0);
    lr_lid.lineTo(B, 0);
    lr_lid.lineTo(0, 0);
    var lr_lid_d = new THREE.ShapeGeometry(lr_lid);

    // ลิ้นขวาล่าง
    var lr_lid_bottom = new THREE.Shape();
    lr_lid_bottom.moveTo(0, 0);
    lr_lid_bottom.lineTo(0, (A * 0.47) | 0);
    lr_lid_bottom.lineTo((B * 0.4) | 0, (A * 0.47) | 0); 
    lr_lid_bottom.bezierCurveTo(
      (B*0.4)|0, // x1
      (A*0.47)|0, // y1
      (B*0.5)|0, // x2
      (A*0.38)|0, // y2
      (B*0.4)|0, // x3
      (A*0.29)|0 // y3
    );
    lr_lid_bottom.lineTo(B, 0);
    lr_lid_bottom.lineTo(0, 0);
    var lr_lid_bottom_d = new THREE.ShapeGeometry(lr_lid_bottom);

    // ลิ้นล่าง
    var lr_bottom = new THREE.Shape();
    lr_bottom.moveTo(0, 0); //เริ่มต้นที่จุด x = 0, y=0
    lr_bottom.lineTo(0, A | 0); //ด้านตรงข้ามมุมฉาก y // 0, 24
    lr_bottom.lineTo((A*0.5)|0, A); // 26, 24
    lr_bottom.lineTo((A*0.5)|0, (A*0.75)|0); // 26, 15

    lr_bottom.lineTo((A*0.25)|0, (A*0.75)|0);
    lr_bottom.lineTo((A*0.25)|0, (A*0.25)|0);
    lr_bottom.lineTo((A*0.5)|0, (A*0.25)|0);

    lr_bottom.lineTo((A*0.5)|0, 0);
    var lr_lid_bottom = new THREE.ShapeGeometry(lr_bottom);

    //ลิ้นซ้ายล่าง
    var lr_bottom_lid = new THREE.Shape();
    lr_bottom_lid.moveTo(0, 0); //เริ่มต้นที่จุด x = 0, y=0
    lr_bottom_lid.lineTo(0, A | 0); //ด้านตรงข้ามมุมฉาก y // 0, 24
    lr_bottom_lid.lineTo((A*0.3)|0, (A*0.7)|0); // 26, 15

    lr_bottom_lid.lineTo((A*0.47)|0, (A*0.7)|0); // 26, 15
    lr_bottom_lid.lineTo((A*0.47)|0, (A*0.3)|0); // 26, 15

    lr_bottom_lid.lineTo((A*0.3)|0, (A*0.3)|0); // 26, 15
    var lr_bottom_lid = new THREE.ShapeGeometry(lr_bottom_lid);

    //******************* A-B_Front_Back_Right_Left *******************

    //B_RIGHT
    var side_B_right = new THREE.Mesh(plane_side_B, material);
    side_B_right.position.x = 50;
    var B_Right = new THREE.Object3D();
    B_Right.position.set(0, 25, 0);
    B_Right.add(side_B_right);

    //A_BACK
    var side_A_back = new THREE.Mesh(plane_side_A, material);
    side_A_back.position.x = -50;
    var A_Back = new THREE.Object3D();
    A_Back.position.set(0, 25, 0);
    A_Back.add(side_A_back);

    //B_LEFT
    var side_B_left = new THREE.Mesh(plane_side_B, material);
    side_B_left.position.x = -150;
    var B_Left = new THREE.Object3D();
    B_Left.position.set(0, 25, 0);
    B_Left.add(side_B_left);

    //A_FRONT
    var side_A_front = new THREE.Mesh(plane_side_A, material);
    side_A_front.position.x = -250;
    var A_Front = new THREE.Object3D();
    A_Front.position.set(0, 25, 0);
    A_Front.add(side_A_front);

    scene.add(B_Right, A_Back, B_Left, A_Front);

    //******************* Pivot *******************

    //Pivot_Back

    //Pivot_Back_Lid
    var pivot_back_lid = new THREE.Mesh(lr_lid_bottom, material);
    pivot_back_lid.rotation.x = Math.PI;
    pivot_back_lid.rotation.z = Math.PI/2;
    pivot_back_lid.position.set((A*-0)|0, 0, 0);

    //Pivot_Right

    //Pivot_Lid_Right
    var pivot_lid_right = new THREE.Mesh(lr_lid_d, material);
    pivot_lid_right.rotation.y = Math.PI;
    pivot_lid_right.position.set(A, C, 0);

      //Pivot_lid_Right_D
    var pivot_lid_right_d = new THREE.Mesh(lr_lid_bottom_d, material);
    pivot_lid_right_d.rotation.x = Math.PI;
    pivot_lid_right_d.rotation.y = Math.PI;
    pivot_lid_right_d.position.set(A, 0, 0);

    //Pivot_Top

    //Pivot_Top_Lid
    var pivot_top_lid = new THREE.Mesh(lid, material);
    pivot_top_lid.position.set(-100, A*1.5|0, 0);
    pivot_top_lid.rotation.x = Math.PI;

    //Pivot_Top
    var pivot_top = new THREE.Mesh(plane_top_bottom, material);
    pivot_top.position.x = -50;
    pivot_top.position.y = A;

    //Pivot_Left

    //Pivot_Lid_Left
    var pivot_lid_left = new THREE.Mesh(lr_lid_d, material);
    pivot_lid_left.position.set((A*-2)|0, C, 0);

    //Pivot_Lid_Left_D
    var pivot_lid_left_d = new THREE.Mesh(lr_lid_bottom_d, material);
    pivot_lid_left_d.rotation.x = Math.PI;
    pivot_lid_left_d.position.set((A*-2)|0, 0, 0);

    //Pivot_Front





    //Pivot_lid_Ground
    var pivot_lid_ground = new THREE.Mesh(lid_ground, material);
    pivot_lid_ground.position.set(-300, 0, 0);
    pivot_lid_ground.rotation.z = Math.PI / 2;
    pivot_lid_ground.rotation.y = Math.PI;



    //Pivot_Bottom

    //Pivot_Bottom_Lid
    var pivot_bottom_lid = new THREE.Mesh(lr_bottom_lid, material);
    pivot_bottom_lid.position.set(-200, 0, 0);
    pivot_bottom_lid.rotation.z = Math.PI / 2;
    pivot_bottom_lid.rotation.x = Math.PI;

    scene.add(
      pivot_back_lid,

      pivot_lid_right,
      pivot_lid_left,

      pivot_top_lid,
      pivot_top,


      pivot_lid_right_d,
      pivot_lid_left_d,
      pivot_lid_ground,

      pivot_bottom_lid
    );

    //* Webgl Render
    renderer = new THREE.WebGLRenderer();
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(w, h);
    document.getElementById("webgl").append(renderer.domElement);

    //* The mouse controls.
    controls = new OrbitControls(camera, renderer.domElement);
    controls.minZoom = 0.5;
    controls.maxZoom = 10;
  };

  const animate = () => {
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
  };

  return (
    <Fragment>
      {init()}
      {animate()}
    </Fragment>
  );
};

export default Three;
