var P = 5; // ความกว้างเฉพาะด้านของฝาเสียบกาว

var leng_lr_lib = A * L;
var edges;
var tween; //

const init = () => {
  scene = new THREE.Scene();
  scene.background = new THREE.Color(0x000000); // เซ็ทตำแหน่งของกล้อง

