import React, { useState } from "react";
import { Slider, InputNumber, Row, Col, Button } from "antd";
import "antd/dist/antd.css";

const IntegerStep = (props) => {
  const [inputValue, setinputValue] = useState(1);

  const onChange = (value) => {
    console.log(value);
    setinputValue(value);
  };

  const rotations1 = () => {
    // console.log(1);
    return props.close(true);
  };

  const rotations2 = () => {
    console.log(2);
  };

  return (
    <div>
      <Button type="primary" onClick={rotations1}>
        พับกล่อง
      </Button>
      <Button type="secondary" onClick={rotations2}>
        แกะกล่อง
      </Button>
      <Row>
        <Col span={12}>
          <Slider
            min={1}
            max={200}
            onChange={onChange}
            value={typeof inputValue === "number" ? inputValue : 0}
          />
        </Col>
        <Col span={4}>
          <InputNumber
            min={1}
            max={200}
            style={{ margin: "0 16px" }}
            value={inputValue}
            onChange={onChange}
          />
        </Col>
      </Row>
    </div>
  );
};

export default IntegerStep;
