    side_A_back.rotation.set(0, 0, (Math.PI / 180) * 90);
    side_A_back.position.x = B / 2;
    side_A_back.position.y = A / 2;
    scene.add(side_A_back);

    var side_Glue_flap = new THREE.Mesh(glue_flap, material);
    side_Glue_flap.rotation.set(0, Math.PI, (Math.PI / 180) * 90);
    side_Glue_flap.position.x = -B;
    scene.add(side_Glue_flap);

    var side_A_front = new THREE.Mesh(plane_A_side, material);
    side_A_front.rotation.set(0, 0, (Math.PI / 180) * 90);
    side_A_front.position.x = -B / 2;
    side_A_front.position.y = A / 2;
    scene.add(side_A_front);

    var side_B_left = new THREE.Mesh(plane_B_side, material);
    side_B_left.rotation.set(0, 0, (Math.PI / 180) * 90);
    side_B_left.position.y = A / 2;
    scene.add(Side_B_left);

    var side_B_right = new THREE.Mesh(plane_B_side, material);
    side_B_right.rotation.set(0, 0, (Math.PI / 180) * 90);
    side_B_right.position.x = A * (25 / A);
    side_B_right.position.y = C * (100 / C);
    scene.add(side_B_right);

    /* จุดหมุน */

