import React, { useState } from "react";
import { Slider, InputNumber, Row, Col } from "antd";
import "antd/dist/antd.css";

const IntegerStep = () => {
  const [inputValue, setinputValue] = useState(1);

  const onChange = (value) => {
    console.log(value);
    setinputValue({
      inputValue: value,
    });
  };

  const { inputValues } = inputValue;

  return (
    <Row>
      <Col span={12}>
        <Slider
          min={1}
          max={20}
          onChange={onChange}
          value={typeof inputValues === "number" ? inputValues : 0}
        />
      </Col>
      <Col span={4}>
        <InputNumber
          min={1}
          max={20}
          style={{ margin: "0 16px" }}
          value={inputValues}
          onChange={onChange}
        />
      </Col>
    </Row>
  );
};

export default IntegerStep;
