import React, { Fragment } from "react";
import * as THREE from "three";
import OrbitControls from "three-orbitcontrols";
const Three = () => {

  const init = () => {
    var A = 52;
    var B = 52;
    var C = 52;
    var w = Window.innerWidth;
    var h = Window.innerHeight / 2;

    var scene = new THREE.Scene();
    // scene.background = new THREE.Color(0xffffff);
    scene.background = new THREE.Color(0x000000);

    //The smaller the first number is, the closer the cube appears
    var camera = new THREE.PerspectiveCamera(50, w / h, 1, 5000);
    camera.position.x = 0;
    camera.position.y = 0;
    camera.position.z = 800;

    const axesHelper = new THREE.AxesHelper(100);
    // scene.add(axesHelper)

    //* ลิ้นด้านล่าง
    var lr_lid_shape_d = new THREE.Shape();
    lr_lid_shape_d.moveTo(0, 0);
    lr_lid_shape_d.lineTo(0, (A * 0.47) | 0); // 0, 24
    lr_lid_shape_d.lineTo((B * 0.5) | 0, (A * 0.47) | 0); // 26, 24
    lr_lid_shape_d.lineTo((B * 0.5) | 0, (A * 0.29) | 0); // 26, 15
    lr_lid_shape_d.lineTo(B, 0); // 52, 0
    lr_lid_shape_d.lineTo(0, 0);

    var lr_lid_d = new THREE.ShapeGeometry(lr_lid_shape_d);

    //* ลิ้นล่างซ้าย
    var side_pp_left_d = new THREE.Mesh(lr_lid_d, material);
    side_pp_left_d.receiveShadow = true;
    side_pp_left_d.castShadow = true;
    side_pp_left_d.rotation.x = Math.PI;
    scene.add(side_pp_left_d);

    //* Webgl Render
    renderer = new THREE.WebGLRenderer();
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(w, h);
    document.getElementById('webgl').append(renderer.domElement);


    //* The mouse controls.
    controls = new OrbitControls(camera, renderer.domElement);
    controls.minZoom = 0.5;
    controls.maxZoom = 10;


  }

  const animate = () => {
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
  }



  return (<Fragment><p>55555</p></Fragment>)
};

export default Three;
