import React, { Fragment } from "react";
import * as THREE from "three";
import OrbitControls from "three-orbitcontrols";

var controls, renderer, scene, camera;

// //* Sub pivot.
var pivot_back;
var pivot_right;
var pivot_right_lid;
var pivot_right_lid_d;
var pivot_left;
var pivot_left_lid;
var pivot_left_lid_d;
var pivot_top;
var pivot_top_lid;
var pivot_buttom;
var pivot_buttom_lid;
var pivot_front;
var pivot_glue_lid;

//* Group pivot.
var pivot_group_right;
var pivot_group_left;
var pivot_group_front;
var pivot_group_top;
var pivot_group_buttom;

//* Main pivot.
var pivot_all;

var A = 100;
var B = 100;
var C = 50;
var D = 0.5;
var w = window.innerWidth;
var h = window.innerHeight;
var L = 0.3;
var P = 5;
var leng_lr_lib = A * L; // ลิ้นกันฝุ่น

const Three = () => {

  const init = () => {
    scene = new THREE.Scene();
    scene.background = new THREE.Color(0x000000);

    //The Smaller The First Number is, The Closer The Cube Appears.
    camera = new THREE.PerspectiveCamera(50, w / h, 1, 5000);
    camera.position.x = 0;
    camera.position.y = 0;
    camera.position.z = 800;

    const axesHelper = new THREE.AxesHelper(100);
    scene.add(axesHelper);

    const material = new THREE.MeshBasicMaterial({
      color: "#FFFFFF",
      side: THREE.DoubleSide,
      wireframe: true,
    });

    // Set Value for Draw Lid Glue
    var glue_lid_shape = new THREE.Shape();
    glue_lid_shape.moveTo(0, 0);
    glue_lid_shape.lineTo(C, 0);
    glue_lid_shape.moveTo(0, 0);
    glue_lid_shape.lineTo(C, 0);
    glue_lid_shape.bezierCurveTo(C, 0, C - C / 35, -(P - P / 35), C - C / 10, -P);
    glue_lid_shape.lineTo(C / 10, -P);
    glue_lid_shape.bezierCurveTo(C / 10, -P, C / 35, -(P - P / 35), 0, 0);

    // ลิ้นกันฝุ่น
    var lr_lid_shape = new THREE.Shape();
    lr_lid_shape.moveTo(0, 0);
    // Front ....................................................
    lr_lid_shape.lineTo(0, (leng_lr_lib * 0.1) | 0);
    lr_lid_shape.lineTo(B * 0.05, (leng_lr_lib * 0.15) | 0);
    lr_lid_shape.lineTo(B * 0.15, (leng_lr_lib * 0.9) | 0);
    // Center ...................................................
    lr_lid_shape.lineTo(B * 0.2, leng_lr_lib);
    lr_lid_shape.lineTo(B, leng_lr_lib);
    // Rear......................................................
    lr_lid_shape.lineTo(B, 0);
    lr_lid_shape.lineTo(0, 0);

    // ฝาเสียบ
    var lid_shape = new THREE.Shape();
    lid_shape.moveTo(0, 0);
    lid_shape.lineTo(A, 0);
    lid_shape.bezierCurveTo(A, 0, A - A / 35, -(P - P / 35), A - A / 10, -P);
    lid_shape.lineTo(A / 10, -P);
    lid_shape.bezierCurveTo(A / 10, -P, A / 35, -(P - P / 35), 0, 0);

    // ******************* Shape Using ******************* //

    var lid_cover = new THREE.ShapeGeometry(lid_shape);
    var lr_lid = new THREE.ShapeGeometry(lr_lid_shape);
    var glue_lid = new THREE.ShapeGeometry(glue_lid_shape);

    var plane_side_A = new THREE.BoxGeometry(A, C, D);
    var plane_side_B = new THREE.BoxGeometry(B, C, D);
    var plane_top_buttom = new THREE.BoxGeometry(A, B, D);

    // ******************* Shape Modify ******************* //

    //เซทฉาก

    var side_A_front = new THREE.Mesh(plane_side_A, material);
    side_A_front.position.x = A/2;
    side_A_front.position.y = C/2;

    var side_A_back = new THREE.Mesh(plane_side_A, material);
    side_A_back.position.x = -A/2;
    side_A_back.position.y = C/2;

    var side_B_right = new THREE.Mesh(plane_side_B, material);
    side_B_right.position.x = B/2;
    side_B_right.position.y = C/2;

    var side_B_left = new THREE.Mesh(plane_side_B, material);
    side_B_left.position.x = -B/2;
    side_B_left.position.y = C/2;

    var side_Top = new THREE.Mesh(plane_top_buttom, material);
    side_Top.position.x = A/2;
    side_Top.position.y = B/2;

    var side_Buttom = new THREE.Mesh(plane_top_buttom, material);
    side_Buttom.position.x = A/2;
    side_Buttom.position.y = -B/2;

    //สร้างจุดหมุน

    var pivot_buttom = new THREE.Object3D();
    pivot_buttom.position.set(-B,0,0);
    pivot_buttom.add(side_Buttom);

    // *
    var pivot_glue_lid = new THREE.Mesh(glue_lid, material);
    pivot_glue_lid.position.set(-C,-C,0);

    // **
    // var pivot_right_lid_cover = new THREE.Mesh(lid_cover, material);
    // pivot_right_lid_cover.position.set(0,0,0);

    var pivot_a_front = new THREE.Object3D();
    pivot_a_front.position.set(0,0,0);
    pivot_a_front.add(side_A_front);

    var pivot_a_back = new THREE.Object3D();
    pivot_a_back.position.set(0,0,0);
    pivot_a_back.add(
      side_A_back,
      pivot_buttom,
      pivot_glue_lid,
      // pivot_right_lid_cover
    );

    var pivot_group_a_back = new THREE.Object3D();
    pivot_group_a_back.position.set(-B,0,0);
    pivot_group_a_back.add(pivot_a_back)

    var pivot_b_left = new THREE.Object3D();
    pivot_b_left.position.set(0,0,0);
    pivot_b_left.add(side_B_left, pivot_group_a_back);

    // *
    // var pivot_right_lid = new THREE.Mesh(lr_lid, material);
    // pivot_right_lid.position.set(0,0,0);

    var pivot_b_right = new THREE.Object3D();
    pivot_b_right.position.set(A,0,0);
    pivot_b_right.add(
      side_B_right,
      // pivot_right_lid
    );

    var pivot_top = new THREE.Object3D();
    pivot_top.position.set(0,C,0);
    pivot_top.add(side_Top);

    var pivot_all = new THREE.Object3D();
    pivot_all.position.set(0,0,0);
    scene.add(pivot_all);
    pivot_all.add(
      pivot_a_front,
      pivot_b_right,
      pivot_b_left,
      pivot_top
    );

    // Webgl Render
    renderer = new THREE.WebGLRenderer();
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(w, h);
    document.getElementById("webgl").append(renderer.domElement);

    // The mouse controls.
    controls = new OrbitControls(camera, renderer.domElement);
    controls.minZoom = 0.5;
    controls.maxZoom = 10;
  };

  const animate = () => {
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
  };

  return (
    <Fragment>
      {init()}
      {animate()}
    </Fragment>
  );
};

export default Three;
