import React, { Fragment } from "react";
import * as THREE from "three";
import OrbitControls from "three-orbitcontrols";

var controls, renderer, scene, camera;
var A = 100;
var B = 200;
var C = 52;
var w = window.innerWidth;
var h = window.innerHeight;
var L = 0.3;
var leng_lr_lib = A * L; //* ลิ้นกันฝุ่น

const Three = () => {
  const init = () => {
    scene = new THREE.Scene();
    // scene.background = new THREE.Color(0xffffff);
    scene.background = new THREE.Color(0x000000);

    //The smaller the first number is, the closer the cube appears
    camera = new THREE.PerspectiveCamera(50, w / h, 1, 5000);
    camera.position.x = 0;
    camera.position.y = 0;
    camera.position.z = 800;

    const axesHelper = new THREE.AxesHelper(100);
    scene.add(axesHelper);

    const material = new THREE.MeshBasicMaterial({
      color: "#FFFFFF",
      side: THREE.DoubleSide,
      wireframe: false,
    });

    //* ลิ้นด้านล่าง
    //Default
    // var lr_lid_shape = new THREE.Shape();
    // lr_lid_shape.moveTo(0, 0); //เริ่มต้นที่จุด x = 0, y=0
    // lr_lid_shape.lineTo(0, (A * 0.47) | 0); //ด้านตรงข้ามมุมฉาก y // 0, 24
    // lr_lid_shape.lineTo((B * 0.5) | 0, (A * 0.47) | 0); //ด้านฉากบน // 26, 24
    // lr_lid_shape.bezierCurveTo(25, 24, 29, 21, 25, 15);
    // lr_lid_shape.lineTo(20,0,25,0,A,0)
    // lr_lid_shape.lineTo(B, 0); //ด้านชิดมุมฉาก // 52, 0

    //New
    // 1
    // var lr_lid_shape = new THREE.Shape();
    // lr_lid_shape.moveTo(0, 0); //เริ่มต้นที่จุด x = 0, y=0
    // lr_lid_shape.lineTo(0, A | 0); //ด้านตรงข้ามมุมฉาก y // 0, 24
    // lr_lid_shape.lineTo(36, A); // 26, 24
    // lr_lid_shape.lineTo(36, 34); // 26, 15

    // lr_lid_shape.lineTo(25, 37);
    // lr_lid_shape.lineTo(25, 15);

    // lr_lid_shape.lineTo(36, 18);
    // lr_lid_shape.lineTo(36, 0); //ด้านชิดมุมฉาก // 52, 0

    // 2
    // var lr_lid_shape = new THREE.Shape();
    // lr_lid_shape.moveTo(0, 0); //เริ่มต้นที่จุด x = 0, y=0
    // lr_lid_shape.lineTo(0, A | 0); //ด้านตรงข้ามมุมฉาก y // 0, 24
    // lr_lid_shape.lineTo(26, 37); // 26, 24
    // lr_lid_shape.lineTo(38, 37); // 26, 15

    // lr_lid_shape.bezierCurveTo(38, 37, 42, 35, 42, 33);
    // lr_lid_shape.lineTo(42, 19);
    // lr_lid_shape.bezierCurveTo(42, 19, 42, 17, 38, 15);
    // lr_lid_shape.lineTo(26, 15); //ด้านชิดมุมฉาก // 52, 0

    //ลิ้นกันฝุ่น
    // var lr_lid_shape = new THREE.Shape();
    // lr_lid_shape.moveTo(0, 0);
    // // Front ....................................................
    // // lr_lid_shape.lineTo(0, (leng_lr_lib * 0.1) | 0);
    // // lr_lid_shape.lineTo(B * 0.05, (leng_lr_lib * 0.15) | 0);
    // lr_lid_shape.lineTo(B * 0.15, (leng_lr_lib * 0.9) | 0);
    // lr_lid_shape.lineTo(B * 0.2, leng_lr_lib);
    // // Center ...................................................
    // lr_lid_shape.lineTo(B * 0.8, leng_lr_lib);
    // // Back .....................................................
    // lr_lid_shape.lineTo(B * 0.85, (leng_lr_lib * 0.9) | 0);
    // // lr_lid_shape.lineTo(B * 0.95, (leng_lr_lib * 0.15) | 0);
    // // Rear......................................................
    // // lr_lid_shape.lineTo(B, (leng_lr_lib * 0.1) | 0);
    // lr_lid_shape.lineTo(B, 0);

    // var lr_lid_shape = new THREE.Shape();
    // lr_lid_shape.moveTo(0, 0);
    // // Front ....................................................
    // lr_lid_shape.lineTo(0, (leng_lr_lib * 0.1) | 0);
    // lr_lid_shape.lineTo(B * 0.05, (leng_lr_lib * 0.15) | 0);
    // lr_lid_shape.lineTo(B * 0.05, (leng_lr_lib * 0.7) | 0);
    // lr_lid_shape.bezierCurveTo(
    //   B * 0.05,
    //   leng_lr_lib,
    //   B * 0.1,
    //   leng_lr_lib,
    //   B * 0.15,
    //   leng_lr_lib
    // );
    // // Center ...................................................
    // lr_lid_shape.lineTo(B * 0.95, leng_lr_lib);
    // // Back .....................................................
    // lr_lid_shape.lineTo(B * 0.95, (leng_lr_lib * 0.1) | 0);
    // lr_lid_shape.lineTo(B, 0);

    //* ลิ้นด้านล่าง
    var lr_lid_shape = new THREE.Shape();
    lr_lid_shape.moveTo(0, 0);
    lr_lid_shape.lineTo(0, (A * 0.47) | 0); // 0, 24
    lr_lid_shape.lineTo((B * 0.5) | 0, (A * 0.47) | 0); // 26, 24
    lr_lid_shape.lineTo((B * 0.5) | 0, (A * 0.29) | 0); // 26, 15
    lr_lid_shape.lineTo(B, 0); // 52, 0
    lr_lid_shape.lineTo(0, 0);

    var lr_lid_d = new THREE.ShapeGeometry(lr_lid_shape);

    // //* ลิ้นล่างซ้าย
    // var side_pp_left_d = new THREE.Mesh(lr_lid_d, material);
    // // side_pp_left_d.rotation.x = Math.PI; หมุนแกน 180 องศา
    // // side_pp_left_d.position.x = 100;
    // scene.add(side_pp_left_d);

    //* Webgl Render
    renderer = new THREE.WebGLRenderer();
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(w, h);
    document.getElementById("webgl").append(renderer.domElement);

    //* The mouse controls.
    controls = new OrbitControls(camera, renderer.domElement);
    controls.minZoom = 0.5;
    controls.maxZoom = 10;
  };

  const animate = () => {
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
  };

  return (
    <Fragment>
      {init()}
      {animate()}
    </Fragment>
  );
};

export default Three;
