import React, { Fragment } from "react";
import Menu from "./menu";
// import THREEBOX from "./three";
// import CARRYBOX from "./carrybox/carry"
// import FOODBOX from "./foodbox/food"
import TRAYBOX from "./traybox/tray"
// import TRAYSBOX from "./traybox/trays"

const Control = () => {
  // Button
  const closeBox = (clb) => {
    console.log("Close");
    return TRAYBOX.rotations1();
  };

  const openBox = (opb) => {
    console.log("Open");
    return TRAYBOX.rotations2();
  };

  // Slider
  const sizeShape = (a, b, c) => {
    console.log(`${a} ${b} ${c}`);
    return TRAYBOX.updateSize(a, b, c);
  };

  // showShape
  const showShape = () => {
    // return THREEBOX.main();
    return TRAYBOX.main();
  };

  return (
    <Fragment>
      {showShape()}
      <Menu
        clb={closeBox}
        opb={openBox}
        size={sizeShape} />
    </Fragment>
  );
};

export default Control;
