import React, { Fragment } from "react";
import * as THREE from "three";
import OrbitControls from "three-orbitcontrols";

var controls, renderer, scene, camera;
var A = 100;
var B = 100;
var C = 52;
var D = 0.5;
var w = window.innerWidth;
var h = window.innerHeight;
var L = 0.3;
var leng_lr_lid = A * L; // ลิ้นกันฝุ่น

const Three = () => {
  const init = () => {
    scene = new THREE.Scene();
    // scene.background = new THREE.Color(0xffffff);
    scene.background = new THREE.Color(0x000000);

    //The smaller the first number is, the closer the cube appears
    camera = new THREE.PerspectiveCamera(50, w / h, 1, 5000);
    camera.position.x = 0;
    camera.position.y = 0;
    camera.position.z = 800;

    const axesHelper = new THREE.AxesHelper(100);
    scene.add(axesHelper);

    const material = new THREE.MeshBasicMaterial({
      color: "#FFFFFF",
      side: THREE.DoubleSide,
      wireframe: true,
    });

    var plane_side_A = new THREE.BoxGeometry(A, C, D);
    var plane_side_B = new THREE.BoxGeometry(B, C, D);
    var plane_top_bottom = new THREE.BoxGeometry(A, B, D);

    //******************* Shape Modify *******************

    // ตัวเสียบ
    var side_pp = new THREE.Mesh(lr_lid_d, material);
    var lr_lid_shape = new THREE.Shape();
    lr_lid_shape.moveTo(0, 0);
    // Front ....................................................
    lr_lid_shape.lineTo(C * 0.15, (leng_lr_lid * 0.9) | 0);
    lr_lid_shape.lineTo(C * 0.2, leng_lr_lid);
    // Center ...................................................
    lr_lid_shape.lineTo(C * 0.8, leng_lr_lid);
    // Back .....................................................
    lr_lid_shape.lineTo(C * 0.85, (leng_lr_lid * 0.9) | 0);
    // Rear......................................................
    lr_lid_shape.lineTo(C, 0);
    var lr_lid_d = new THREE.ShapeGeometry(lr_lid_shape);

    // ลิ้นกันฝุ่น
    var lr_lid_shape = new THREE.Shape();
    lr_lid_shape.moveTo(0, 0);
    // Front ....................................................
    lr_lid_shape.lineTo(0, (leng_lr_lib * 0.1) | 0);
    lr_lid_shape.lineTo(B * 0.05, (leng_lr_lib * 0.15) | 0);
    lr_lid_shape.lineTo(B * 0.05, (leng_lr_lib * 0.7) | 0);
    lr_lid_shape.bezierCurveTo(
      B * 0.05,
      leng_lr_lib,
      B * 0.1,
      leng_lr_lib,
      B * 0.15,
      leng_lr_lib
    );
    // Center ...................................................
    lr_lid_shape.lineTo(B * 0.95, leng_lr_lib);
    // Back .....................................................
    lr_lid_shape.lineTo(B * 0.95, (leng_lr_lib * 0.1) | 0);
    lr_lid_shape.lineTo(B, 0);
    var lr_lid_d = new THREE.ShapeGeometry(lr_lid_shape);

    //******************* A-B_Front_Back_Right_Left *******************

    //B_RIGHT
    var side_B_right = new THREE.Mesh(plane_side_B, material);
    side_B_right.position.x = 50;
    var B_Right = new THREE.Object3D();
    B_Right.position.set(0, 25, 0);
    B_Right.add(side_B_right);

    //A_BACK
    var side_A_back = new THREE.Mesh(plane_side_A, material);
    side_A_back.position.x = -50;
    var A_Back = new THREE.Object3D();
    A_Back.position.set(0, 25, 0);
    A_Back.add(side_A_back);

    //B_LEFT
    var side_B_left = new THREE.Mesh(plane_side_B, material);
    side_B_left.position.x = -150;
    var B_Left = new THREE.Object3D();
    B_Left.position.set(0, 25, 0);
    B_Left.add(side_B_left);

    //A_FRONT
    var side_A_front = new THREE.Mesh(plane_side_A, material);
    side_A_front.position.x = -250;
    var A_Front = new THREE.Object3D();
    A_Front.position.set(0, 25, 0);
    A_Front.add(side_A_front);

    scene.add(B_Right, A_Back, B_Left, A_Front);

    //******************* Pivot *******************

    //Pivot_lid_Right
    var pivot_lid_right_shape = new THREE.Mesh(lr_lid_d, material);
    var pivot_lid_right = new THREE.Object3D();
    pivot_lid_right_shape.position.set(0, C, 0);
    pivot_lid_right.add(pivot_lid_right_shape);

    //Pivot_lid_Right_D
    var pivot_lid_right_d_shape = new THREE.Mesh(lr_lid_d, material);
    var pivot_lid_right_d = new THREE.Object3D();
    pivot_lid_right_d.position.set(0, C, 0);
    pivot_lid_right_d_shape.rotation.x = Math.PI;
    pivot_lid_right_d.add(pivot_lid_right_d_shape);

    //Pivot_lid_Top
    var pivot_lid_top_shape = new THREE.Mesh(lr_lid_d, material);
    pivot_lid_top_shape.position.set(-50, A*1.5|0, 0);
    var pivot_lid_top = new THREE.Object3D();

    //Pivot_Top
    var pivot_top_shape = new THREE.Mesh(plane_top_bottom, material);
    pivot_top_shape.position.x = -50;
    pivot_top_shape.position.y = A;
    var pivot_top = new THREE.Object3D();
    pivot_top.add(pivot_top_shape,pivot_lid_top_shape);

    //Pivot_Lid_Ground
    var pivot_lid_ground_shape = new THREE.Mesh(lr_lid_d, material);
    pivot_lid_ground_shape.position.set(-300, 0, 0);
    pivot_lid_ground_shape.rotation.z = Math.PI / 2;
    var pivot_lid_ground = new THREE.Object3D();
    pivot_lid_ground.add(pivot_lid_ground_shape);

    scene.add(
      pivot_lid_right_shape,
      pivot_lid_right_d_shape,
      pivot_lid_top_shape,
      pivot_top_shape,
      pivot_lid_ground_shape
    );

    //* Webgl Render
    renderer = new THREE.WebGLRenderer();
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(w, h);
    document.getElementById("webgl").append(renderer.domElement);

    //* The mouse controls.
    controls = new OrbitControls(camera, renderer.domElement);
    controls.minZoom = 0.5;
    controls.maxZoom = 10;
  };

  const animate = () => {
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
  };

  return (
    <Fragment>
      {init()}
      {animate()}
    </Fragment>
  );
};

export default Three;
